import { createContext, useContext, useEffect, useState } from "react";
import { onAuthStateChanged } from "firebase/auth";
import { auth, db } from "./firebase";
import { onSnapshot, doc, getDoc, getDocs, collection } from "firebase/firestore";

const ProManage = createContext();

// the context for the entire application
const ProManageContext = ({ children }) => {
  const [alert, setAlert] = useState({
    open: false,
    message: "",
    type: "success",
  });

  const [user, setUser] = useState(null);
  const [availIdeas, setAvailIdeas] = useState([
    {
      id: 1,
      name: 'Face detection',
      description: 'One of the best ideas to start experimenting you hands-on computer science projects for students is face detection software. This project focuses on building a face detection software using the OpenCV library. The face detection program will be modelled in a way that it can detect faces in live stream videos from webcam or video files stored in a PC’s local storage. The software uses pre-trained XML classifiers to detect faces in real-time and track them. You can also use different classifiers to identify various objects through this detection program',
      type: 'Technical Computer Science or Information Technology Projects',
    },
    {
      id: 2,
      name: 'Online auction system',
      description: 'In an online auction, buyers and sellers engage in transactional business, wherein buyers purchase items through price bidding. Here, the bids have a starting price and an ending time. Potential buyers who place the highest bidding price for an item are declared the winners and owners of particular items. ',
      type: 'Business-Oriented Computer Science or Information Technology Projects',
    },
    {
      id: 3,
      name: ' Evaluation of academic performance',
      description: 'This project involves the creation of an evaluation system that can analyze the academic performance of students by utilizing the fuzzy logic method. In the fuzzy logic method, you will consider three parameters, namely, attendance, internal marks, and external marks, to evaluate the final academic performance of students of an institution. The fuzzy inference system is much more accurate than conventional techniques.',
      type: 'Business-Oriented Computer Science or Information Technology Projects',
    },
    {
      id: 4,
      name: 'e-Authentication system ',
      description: 'This project focuses on building an e-Authentication system using a combination of QR code and OTP for enhanced security. The e-Authentication system is designed to avoid hacking of accounts through shoulder surfing and misuse of login credentials. To be able to use the system, a user has to first register in the system by entering the basic registration details (name, address, zip code, etc.).',
      type: 'Research Projects',
    }
  ])

  // setting the user state to the user that is currently logged in
  useEffect(() => {
    onAuthStateChanged(auth, (user) => {
      if (user) setUser(user);
      else setUser(null);
    });
  }, []);

  // saving the new ideas from the available ideas add form to the firestore database
  useEffect(() => {
    const ideaRef = collection(db, "Ideas");
    if (user) {
      const getAvailIdeas = async  () => {
        const data = await getDocs(ideaRef)
        if (data.exists()) {
          setAvailIdeas(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
        }
        else {
          console.log("No Available Ideas, Please check back later");
        }
      };


      getAvailIdeas();

    }
  }, [user]);

  return (
    <ProManage.Provider
      value={{
        alert,
        setAlert,
        user,
        availIdeas,
      }}
    >
      {children}
    </ProManage.Provider>
  );
};

export default ProManageContext;

export const ProManageState = () => {
  return useContext(ProManage);
};
