import { useEffect, useState } from "react";
import TextField from '@mui/material/TextField';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import { makeStyles } from "@material-ui/core";
import { ProManageState } from '../../ProManageContext';
import { db } from '../../firebase';
import { doc, setDoc } from "firebase/firestore";
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';

const useStyles = makeStyles((theme) => ({
    Form: {
        flex: 1,
        backgroundColor: theme.palette.background.paper,
    },
    Name: {
        padding: 24,
        paddingTop: 0,
        display: "flex",
        gap: 20,
    },
    Description: {
        padding: 24,
        paddingTop: 0,
        display: "flex",
        gap: 20,
    },
    Select: {
        padding: 24,
        paddingTop: 0,
        display: "flex",
        gap: 20,
    },
    Btn: {
        padding: 24,
        paddingTop: 0,
        display: "flex",
        gap: 20,
    }
}));

const AddIdea = () => {
    const classes = useStyles();
    const { availIdeas, setAlert } = ProManageState();
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [type, setType] = useState('');
   
    const addToAvailIdeas = async () => {
        const ideaRef = doc(db, "Ideas",);
        const idea = {
            id: 7,
            name,
            description,
            type,
        }
        try {
        await setDoc(
            ideaRef,
            { availIdeas: availIdeas ? [...availIdeas, idea?.id] : [idea?.id] },
            { merge: true }
        );
        setAlert({
            open: true,
            message: `${idea.name} Added to the Available Ideas!`,
            type: "success",
        });
        } catch (error) {
        setAlert({
            open: true,
            message: error.message,
            type: "error",
        });
        }
    };

    return (
        <Box component = 'form' className={classes.Form}>
            <div className={classes.Name}>
                <TextField 
                    id="ideaName" 
                    label="Name of Idea" 
                    variant="standard"
                    value={name} 
                    onChange={(e) => setName(e.target.value)}
                    fullWidth
                />
            </div>
            <div className={classes.Description}>
                <TextField 
                    id="ideaDescription"
                    value={description}
                    label="Description"
                    variant="standard"
                    onChange={(e) => setDescription(e.target.value)}
                    fullWidth
                />
            </div>
            <div className={classes.Select}>
                <Select
                    id="type"
                    label="Type"
                    value={type}
                    onChange={(e) => setType(e.target.value)}
                    >
                    <MenuItem value="1"> 
                        Technical Computer Science or Information Technology Projects
                    </MenuItem>
                    <MenuItem value="2">
                        Business-Oriented Computer Science or Information Technology Projects
                    </MenuItem>
                    <MenuItem value="3">
                        Research Projects
                    </MenuItem>
                    <MenuItem value="4">
                        Entrepreneurial or Student-Proposed Projects
                    </MenuItem>
                </Select>
            </div>
            <div className={classes.Btn}>
            <Button
                variant="contained"
                style={{
                width: 85,
                height: 40,
                marginLeft: 15,
                color: "black",
                backgroundColor: "#EEBC1D",
                }}
                onClick = {addToAvailIdeas}
            >
                Add
            </Button>
            </div>
        </Box>
    )
}

export default AddIdea
